function deleteCookie(){document.cookie="wcm_uuid_cookie=; expires=Thu, 01 Jan 1970 00:00:00 UTC";window.location="/index.php"}function getCookie(e){var t=e+"=";var n=document.cookie.split(";");for(var r=0;r<n.length;r++){var i=n[r];while(i.charAt(0)==" "){i=i.substring(1)}if(i.indexOf(t)!=-1){return i.substring(t.length,i.length)}}return""}function setButtonColor(e,t){var n=document.getElementById(e);if(t===false){n.style.backgroundColor="transparent";n.style.border="1px solid #C8C8C8"}else if(t===true){n.style.backgroundColor="#ff0000";n.style.border="none"}}function setLedStatus(e,t){if(t===false){document.getElementById(e+"_On").className="btn btn-default";document.getElementById(e+"_Off").className="btn btn-info active"}else if(t===true){document.getElementById(e+"_On").className="btn btn-success active";document.getElementById(e+"_Off").className="btn btn-default"}switch(e){case"led1":pageLed1Status=t;break;case"led2":pageLed2Status=t;break;case"led3":pageLed3Status=t;break;case"led4":pageLed4Status=t;break}}function setPotentiometerValue(e){var t=Math.round(e*100/1024);document.getElementById("potentiometerBarValue").setAttribute("aria-valuenow",t);document.getElementById("potentiometerBarValue").style.width=t+"%";document.getElementById("potentiometerTextValue").innerHTML=e}function updateAMIStatus(){var e=getCookie("wcm_uuid_cookie");if(e===""){window.location="/index.php";return}var t=new XMLHttpRequest;t.open("GET","json_wcm_get_status.php?uuid="+e,true);t.send();t.onreadystatechange=function(){if(t.readyState===4){var e=JSON.parse(t.response);if(e.mchp.device.status===200&&buttonPressed===false){setButtonColor("button1",e.mchp.device.data.button1);setButtonColor("button2",e.mchp.device.data.button2);setButtonColor("button3",e.mchp.device.data.button3);setButtonColor("button4",e.mchp.device.data.button4);setLedStatus("led1",e.mchp.device.data.led1);setLedStatus("led2",e.mchp.device.data.led2);setLedStatus("led3",e.mchp.device.data.led3);setLedStatus("led4",e.mchp.device.data.led4);setPotentiometerValue(e.mchp.device.data.potentiometer)}}}}function onLedPress(e,t){clearInterval(pageTimer);buttonPressed=true;var n=getCookie("wcm_uuid_cookie");if(n===""){window.location="/index.php";return}setLedStatus(e,t);var r=JSON.stringify({mchp:{device:{"device-type":2,uuid:n,status:200,message:"Sending Data",data:{button1:null,button2:null,button3:null,button4:null,led1:pageLed1Status,led2:pageLed2Status,led3:pageLed3Status,led4:pageLed4Status,potentiometer:null}}}});var i=new XMLHttpRequest;i.open("POST","json_wcm_post_status.php",true);i.setRequestHeader("Content-type","application/json");i.send(r);i.onreadystatechange=function(){if(i.readyState===4){buttonPressed=false;var e=JSON.parse(i.response);if(e.mchp.device.status===200){pageTimer=setInterval(updateAMIStatus,UPDATE_RATE)}else{alert("Database write error")}}}}const UPDATE_RATE=50;window.onload=function(){var e=location.pathname;switch(e){case"/":case"/index.php":$(".navbar-nav").find("li:eq(0)").addClass("active");var t=getCookie("wcm_uuid_cookie");if(t!=""){buttonPressed=false;updateAMIStatus();pageTimer=setInterval(updateAMIStatus,UPDATE_RATE)}break;case"/notes.php":$(".navbar-nav").find("li:eq(1)").addClass("active");break;case"/setup.php":$(".navbar-nav").find("li:eq(2)").addClass("active");break}var n=getCookie("wcm_uuid_cookie");if(n!=""){$(".navbar-text").text("WCM UUID: "+n)}}